CREATE DEFINER=`root`@`localhost` PROCEDURE `user_status`(IN for_user_id INT)
BEGIN
SELECT
tb1.id_deal,
IF(tb3.user_id = for_user_id, 'seller', 'buyer') role_in_deal,
c_1.name currency_on_sell,
tb1.amount_sell,
c_2.name currency_on_buy,
tb1.amount_buy,
tb1.from_account_id,
tb1.to_account_id,
tb3.user_id 'seller',
tb2.user_id 'buyer'
FROM deal_active tb1
JOIN currency c_1 ON tb1.currency_id_sell = c_1.id
JOIN currency c_2 ON tb1.currency_id_buy = c_2.id
JOIN account tb2 ON tb2.id = tb1.to_account_id
JOIN account tb3 ON tb3.id = tb1.from_account_id
WHERE tb2.user_id = for_user_id OR tb3.user_id = for_user_id
ORDER BY id_deal;
END ;;
